#ifndef __BNBCMDTAGS_HPP__
#define __BNBCMDTAGS_HPP__

/**
 * Special symbols for bracketing command
 */

#define BNB_COMMAND_START '['

#define BNB_COMMAND_END ']'

/**
 * XML tags for describing command
 */

#define BNB_COMMAND_COMMAND "command"

#define BNB_COMMAND_NAME "name"

#define BNB_COMMAND_ID "id"

#define BNB_COMMAND_TYPE "type"

#define BNB_COMMAND_STATUS "status"

#define BNB_COMMAND_DESCRIPTION "description"

#define BNB_COMMAND_CODE "code"

/**
 * XML tags for storing algorithmic information (solutions and sets)
 */

#define BNB_SOLUTION_SURFACE "pes"

#define BNB_SOLUTION "solu"

#define BNB_SET "set"

#define BNB_GRADIENT "grad"

#define BNB_EIGEN "eigen"

#define BNB_SPOINT_KIND "kind"

#define BNB_NORM "norm"

#define BNB_X "x"

#define BNB_F "f"

#endif
